import "./card.css";
import { FaTrash } from "react-icons/fa";
function callbackDelete(e) {
  const event = e.target;
  const card = event.closest("li");
  console.log(event);
}
function Card({ description, value, type }) {
  return (
    <li className="card">
      <div className="card-div1">
        <p>{description}</p>
        <p>{type}</p>
      </div>
      <div className="card-div2">
        <p>{value}</p>
        <div className="cursor" onClick={callbackDelete}>
          <FaTrash />
        </div>
      </div>
    </li>
  );
}
export default Card;
